// get blend API is at /api/v1/blends/:id
import axios from 'axios'
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const BlendDetail = () => {
  const { id } = useParams();
  const [blend, setBlend] = useState(null)
  const [spice, setSpice] = useState(null)

  const [name, setName] = useState('')

  useEffect(async () => {
    const { spices } = await axios.get(`/api/v1/spices/${id}`);
    setSpice(spices);
    console.log("useEffect running on blends")
    const { blends } = await axios.get(`/api/v1/blends/${id}`);
    setBlend(blends);
  }, []);

  return (
    <div>
      <h2>Blend Detail Page</h2>
      {blend && (
        <div>
          <div>Blend Name: {blend.name}</div>
          <div>Description: {blend.description}</div>
          <div>Spices in Blend: {blend.spices.map((item) => {
          return <div>item.id</div>
          })}</div>
          {/* <div>Spice Cost: {spice.price}</div>
          <div>Spice Heat Level: {spice.heat}</div> */}
        </div>
      )}
    </div>
  );
};

export default BlendDetail;
