  const [name, setName] = useState("")
  const [recipes, setRecipes] = useState([])

  useEffect(async() => {
    const { data } = await axios.get(`/api/v1/spices/${id}`);
    setSpice(data)
    setName(data.name)
  }, [])



  async function recipeArr() {
    const data = await recipeArr()
