import axios from 'axios'
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

const SpiceDetail = () => {

  const { id } = useParams()
  const [spice, setSpice] = useState(null)

  useEffect(async() => {
    const { data} = await axios.get(`/api/v1/spices/${id}`);
    setSpice(data)
  }, [])

  const getData = (search) => {
    axios.get(`${'https://cors-anywhere.herokuapp.com/'}https://api.yelp.com/v3/businesses/search?`, {
   headers: {
       Authorization: `Bearer ${token}`
       },
   params: {
       term: search,
       location: "Utah County"
       }
   })
   .then(res => {
       setSearchResults(res.data)
       setMappedData(res.data.businesses.map(item => {
           return (
               <div className="business-list" >
                    <img src={item.image_url} alt={item.name} height="150px" width="150px" style={{borderRadius:7}}></img>
                   <div key={item.id} className="business-list-text">
                       <Link to="/business"  onClick={() => getBusiness(item.id)}>{item.name} </Link>
                       <p >{item.location.city}</p>
                   </div>
               </div>
           )
       }))
   })
   .catch(err => console.log(err))
}


  return (
    <div>
      <h2>Spice Detail Page</h2>
      {
        spice && <div>
          <div>Spice Name: {spice.name}</div>
          <div>Spice Color: {spice.color}</div>
          <div>Spice Cost: {spice.price}</div>
          <div>Spice Heat Level: {spice.heat}</div>
        </div>  
      }
    </div>
  )
}

export default SpiceDetail
